binding.script:
if (getprop("sim/model/firetank/arm"))
  {
    if (getprop("/sim/model/firetank/flexhose/hosehitsground"))
      screen.log.write("Damage will occur if Snorkel is deployed this low!");
    else
      if (getprop("sim/model/firetank/deployflexhose") == 1 and getprop("sim/model/firetank/deployflexhose/position-norm") == 1)
        doors.flexhose.toggle();
      else
        if (getprop("sim/model/firetank/deployflexhose") == 0 and getprop("sim/model/firetank/deployflexhose/position-norm") == 0)
          screen.log.write("Hose already deployed!");
  }
else
  screen.log.write("System must be armed first!");

if (getprop("sim/model/firetank/arm"))
  {
    if (getprop("/sim/model/firetank/flexhose/hosehitsground"))
      screen.log.write("Damage will occur if Snorkel is deployed this low!");
    else
      if ((getprop("sim/model/firetank/deployramscoop") == 0 and getprop("sim/model/firetank/deployramscoop/position-norm") == 1) or (getprop("sim/model/firetank/deployramscoop") == 1 and getprop("sim/model/firetank/deployramscoop/position-norm") == 0))
        doors.ramscoop.toggle();
  }
else
  screen.log.write("System must be armed first!");
